SELECT vii.member_id as memberId,
vii.account_id as accountId,
vii.symbol, sum(vii.buy_price)::numeric(17,8) AS buyPrice, sum(vii.sell_price)::numeric(17,8) AS sellPrice, sum(vii.income)::numeric(17,8) AS income, sum(vii.fees)::numeric(17,8) AS fees, count(*) as sellCount FROM aventador.view_infinite_income vii WHERE vii.member_id = #{memberId,jdbcType=INTEGER}
AND vii.account_id = #{accountId,jdbcType=INTEGER}
AND vii.sell_date >= #{sellDateStart,jdbcType=DATE} AND vii.sell_date <= #{sellDateEnd,jdbcType=DATE}
GROUP BY vii.member_id,
vii.account_id, vii.symbol ORDER BY count(*) desc

SELECT vii.member_id as memberId,
vii.account_id as accountId,
to_char(vii.sell_date::timestamp with time zone, 'YYYY-MM'::text) AS monthly, sum(vii.buy_price)::numeric(17,8) AS buyPrice, sum(vii.sell_price)::numeric(17,8) AS sellPrice, sum(vii.income)::numeric(17,8) AS income, sum(vii.fees)::numeric(17,8) AS fees, count(*) as sellCount FROM aventador.view_infinite_income vii WHERE vii.member_id = #{memberId,jdbcType=INTEGER}
AND vii.account_id = #{accountId,jdbcType=INTEGER}
AND vii.sell_date >= #{sellDateStart,jdbcType=DATE} AND vii.sell_date <= #{sellDateEnd,jdbcType=DATE}
GROUP BY vii.member_id,
vii.account_id, (to_char(vii.sell_date::timestamp with time zone, 'YYYY-MM'::text)) ORDER BY (to_char(vii.sell_date::timestamp with time zone, 'YYYY-MM'::text)) ${order}
